//       .catch((err) => dispatch(loginFailureAction(err)));
//   };
// };
/////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////
export const loginRequestAction = (data) => {
  return { type: "LOG_IN_REQUEST", data };
};

export const logoutRequestAction = () => {
  return { type: "LOG_OUT_REQUEST" };
};

const reducer = (state = initialState, action) => {
  switch (action.type) {
    case "LOG_IN_REQUEST": {
      console.log("logged in");
      return {
        ...state,
        isLoggingIn: true,
        me: action.data,
      };
    }
    case "LOG_IN_SUCCESS": {
      return {
        ...state,
        isLoggingIn: false,
        isLoggedIn: true,
        me: { ...action.data, nickname: "wincow" },
      };
    }
    case "LOG_IN_FAILURE": {
      console.log("logged in");
      return {
        ...state,
        isLoggingIn: false,
        isLoggedIn: false,
        me: action.data,
      };
    }
    case "LOG_OUT_REQUEST": {
      return {
        ...state,
        isLoggingOut: true,
        me: null,
      };
    }
    case "LOG_OUT_SUCCESS": {
      return {
        ...state,
        isLoggingOut: false,
        isLoggedIn: false,
        me: null,
      };
    }
    case "LOG_OUT_FAILURE": {
      return {
        ...state,
        isLoggingOut: false,
        me: null,
      };
    }
    default: {
      return { ...state };
    }
  }
};

export default reducer;
