import { HYDRATE } from "next-redux-wrapper";

const initialState = {
  user: {},
  post: {},
};

// async action creater

// 이전 상태와 액션을 통해 다음 상태를 만드는 함수인 거시다.
const rootReducer = (state = initialState, action) => {
  switch (action.type) {
    case HYDRATE:
      console.log(HYDRATE);
      return { ...state, ...action.payload };

    default:
      return state;
  }
};

export default rootReducer;
